        ans = 0

        # Iterate over all possible subsets of branches using a bitmask
        for mask in range(1 << n):

            # Initialize the graph with 'infinity' distances for each pair of branches
            g = [[float('inf')] * n for _ in range(n)]

            # Update the graph with available roads between branches in the current subset
            for u, v, w in roads:
                # If both branches u and v are in the subset (represented by mask), consider this road
                if (mask >> u & 1) and (mask >> v & 1):
